script:
frappe.ui.form.on('Goal', {
	refresh(frm) {
		// your code here
		if (frm.doc.custom_is_sales_person) {
		    console.log('SALES PERSON');
    	    frappe.call({
                method: 'xerebra_hr.xerebra_hr.util.appraisal.get_sales_goal_progress', args: {
                    'employee' : frm.doc.employee 
                },
                callback: function (r) {
                    console.log('PROGRESS=' + r.message)
                    console.log('FRM PROGRESS=' + frm.doc.progress)
                    if (frm.doc.progress != r.message){
                        frm.doc.progress = r.message
                        frm.refresh_field('progress')
                        frm.dirty()
                        frm.save()
                        }
                }
            })
		}
	}
})
